insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Der Lehrer ist fair - gerecht', 1, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Der Lehrer ist humorvoll - fröhlich', 2, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Der Lehrer ist hilfsbereit - unterstützend', 3, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Der Lehrer ist freundlich', 4, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Der Lehrer ist engagiert - aktiv', 5, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Der Lehrer ist verständnisvoll', 6, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Der Lehrer ist ehrlich - aufrichtig', 7, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Der Lehrer ist kooperativ - kontaktfreudig', 8, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Der Lehrer ist tolerant - lässt Kritik zu', 9, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Er kann auch schwierige Fragen beantworten, weil er sich in seinem Fachgebiet gut auskennt.', 10, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Für eine gute Note ist es am wichtigsten, wie sympathisch man dem Lehrer ist - die Leistung ist Nebensache.', 11, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Ich denke, dieser Lehrer kennt mich persönlich schon sehr gut, sowohl in meinen Stärken wie in meinen Schwächen.', 12, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Er bemüht sich, uns der Leistung und dem Benehmen entsprechend zu loben weder zu ermahnen und niemanden zu bevorzugen.', 13, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Bei diesem Lehrer ist mir oft nicht klar, worauf es bei seinen Prüfungen ankommt oder wie meine Note zustande kommt.', 14, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Ich glaube, dass ich durch seinen Unterricht schon viel Neues gelernt habe.', 15, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Sein Unterricht ist interessant und attraktiv, weil er sich um Abwechslung und Praxisnähe bemüht.', 16, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Er verlangt häufig zuviel von uns und deswegen fühle ich mich beim Lernen oft überfordert und gestresst.', 17, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Der Lehrer erklärt im Unterricht so verständlich und klar, dass ich alles Wichtige gut verstehe und begreife.', 18, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn ich einen Fehler mache oder mich schlecht benehme, werde ich von diesem Lehrer vor der ganzen Klasse beschimpft oder lächerlich gemacht.',  19, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Ich freue mich, wenn dieser Lehrer zum Unterricht in unsere Klasse kommt.', 20, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Er erklärt uns oft, wie der Lehrnstoff mit anderen Fächern oder mit praktischen Problemen zusammenhängt.', 21, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn ich bei ihm etwas richtig oder gut gemacht habe, so werde ich dafür auch entsprechend gelobt.', 22, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Bevor er Hausübungen oder etwas zu lernen aufgibt, erkundigt er sich, ob wir schon andere Hausübungen erhalten haben oder Prüfungen bevor stehen.',  23, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Er freut sich, wenn es ihm gelungen ist uns etwas Neues oder Schwieriges beizubringen.', 24, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Dieser Lehrer schafft es oft nicht, die zum Arbeiten nötige Ruhe und Ordnung in unserer Klasse herzustellen.', 25, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn ich Schwierigkeiten beim Lernen habe, kann ich auf die Hilfe und das Verständnis des Lehrers zählen.', 26, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Er bemüht sich sehr, uns in seinem Fah auch aktuelle, moderne und lebensnahe Informationen und Beispiele zu geben.', 27, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn ich einen guten Einfall habe und etwas sagen will, dann beachtet mich der Lehrer oft gar nicht.', 28, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Durch sein Engagement und seinen Einsatz gelingt es dem Lehrer uns für sein Fach besonders zu interessieren und zu aktivieren.', 29, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Der Lehrer achtet darauf, dass wir Schüler im Unterricht auch unsere eigenen Ideen verwirklichen können.', 30, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn mich bei diesem Lehrer etwas stört, so kamm ich das offen sagen, ohne dass ich nachteile befürchten muss.', 31, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Dieser Lehrer beschäftigt sich hauptsächlich mit den guten SchülerInnen in der Klasse.', 32, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn er Hausübungen gibt, dann kann ich diese normalerweise selbstständig und ohne Hilfe anderer Personen bewältigen. ', 33, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn ich etwas falsch mache - zum Beispiel bei der Hausübung - dann erklärt er mir genau, wie man es richtig macht.', 34, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Er bringt mir in diesem Fach etwas bei, was für mich im späteren Leben oder im Beruf wichtig sein wird.', 35, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Durch hohen Lernanforderungen des Lehrers habe ich oft große Angst, bei seinen Prüfungen zu versagen.', 36, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Die Noten, die ich bei diesem Lehrer bekomme, entsprechen meinen tatsächlichen Leistungen.', 37, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Ich würde mir wünschen, dass mich dieser Lehrer auch im nächsten Jahr wieder unterrichtet.', 38, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Dieser Lehrer ist mir sympathisch.', 39, 0, 1);
insert into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wie zufrieden bist du mit diesem Lehrer insgesamt?', 40, 0, 1);
