insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Für eine gute Note ist es am wichtigsten, wie sympathisch man der Lehrerinn ist - die Leistung ist Nebensache.', 11, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Ich denke, diese Lehrerinn kennt mich persönlich schon sehr gut, sowohl in meinen Stärken wie in meinen Schwächen.', 12, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Sie bemüht sich, uns der Leistung und dem Benehmen entsprechend zu loben oder zu ermahnen und niemanden zu bevorzugen.', 13, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Bei dieser Lehrerinn ist mir oft nicht klar, worauf es bei seinen Prüfungen ankommt oder wie meine Note zu stande kommt.', 14, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Ich glaube, dass ich durch ihren Unterricht schon viel Neues gelernt habe.', 15, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Ihr Unterricht ist interessant und attraktiv, weil er sich um Abwechslung und Praxisnähe bemüht.', 16, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Sie verlangt häufig zuviel von uns, und deswegen fühle ich mich beim Lernen oft überfordert und gestresst.', 17, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Die Lehrerinn erklärt im Unterricht so verständlich und klar, dass ich alles Wichtige gut verstehe und begreife.', 18, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn ich einen Fehler mache oder mich schlecht benehme, werde ich von dieser Lehrerinn vor der ganzen Klasse beschimpft oder lächerlich gemacht.', 19, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Ich freue mich, wenn diese Lehrerinn zum Unterricht in unsere Klasse kommt.', 20, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Sie erklärt uns oft, wie der Lehrnstoff mit anderen Fächern oder mit praktischen Problemen zusammenhängt.', 21, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn ich bei ihr etwas richtig oder gut gemacht habe, so werde ich dafür auch entsprechend gelobt.', 22, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Bevor sie Hausübungen oder etwas zu lernen aufgibt, erkundigt er sich, ob wir schon andere Hausübungen erhalten haben oder Prüfungen bevor stehen.', 23, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Sie freut sich, wenn es ihr gelungen ist uns etwas Neues oder Schwieriges beizubringen.', 24, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Diese Lehrerinn schafft es oft nicht, die zum Arbeiten nötige Ruhe und Ordnung in unserer Klasse herzustellen.', 25, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn ich Schwierigkeiten beim Lernen habe, kann ich auf die Hilfe und das Verständnis der Lehrerinn zählen.', 26, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Sie bemüht sich sehr, uns in seinem Fach auch aktuelle, moderne und lebensnahe Informationen und Beispiele zu geben.', 27, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn ich einen guten Einfall habe und etwas sagen will, dann beachtet mich die Lehrerinn oft gar nicht.', 28, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Durch ihr Engagement und seinen Einsatz gelingt es der Lehrerinn uns für sein Fach besonders zu interessieren und zu aktivieren.', 29, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Die Lehrerinn achtet darauf, dass wir Schüler im Unterricht auch unsere eigenen Ideen verwirklichen können.', 30, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn mich bei dieser Lehrerinn etwas stört, so kann ich das offen sagen, ohne dass ich nachteile befürchten muss.', 31, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Diese Lehrerinn beschäftigt sich hauptsächlich mit den guten SchülerInnen in der Klasse.', 32, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn sie Hausübungen gibt, dann kann ich diese normalerweise selbstständig und ohne Hilfe anderer Personen bewältigen. ', 33, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Wenn ich etwas falsch mache - zum Beispiel bei der Hausübung - dann erklärt sie mir genau, wie man es richtig macht.', 34, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Sie bringt mir in diesem Fach etwas bei, was für mich im späteren Leben oder im Beruf wichtig sein wird.', 35, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Durch hohen Lernanforderungen der Lehrerinn habe ich oft große Angst, bei ihren Prüfungen zu versagen.', 36, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Die Noten, die ich bei dieser Lehrerinn bekomme, entsprechen meinen tatsächlichen Leistungen.', 37, 0, 2);
insert  into lq_question(q_text, q_seq_no, q_type_id, Q_QUESTIONNAIRE_ID) values ('Ich würde mir wünschen, dass mich diese Lehrerinn auch im nächsten Jahr wieder unterrichtet.', 38, 0, 2);
